void add(String newcode) {
    maybeEndStatement();

    if (newcode.length() == 0) {
      return;
    }

    char c = newcode.charAt(0);
    if ((isWordChar(c) || c == '\\') &&
        isWordChar(getLastChar())) {
      append(" ");
    } else if (c == '/' && getLastChar() == '/') {
      append(" ");
    }

    append(newcode);
  }

