static boolean isLiteralValue(Node n) {
    switch (n.getType()) {
      case Token.ARRAYLIT:
      case Token.OBJECTLIT:
      case Token.REGEXP:
        for (Node child = n.getFirstChild(); child != null;
             child = child.getNext()) {
          if (!isLiteralValue(child)) {
            return false;
          }
        }
        return true;

      default:
        return isImmutableValue(n);
    }
  }
