/**
   * @return true if this node marks the start of a new basic block
   */

code:
private static boolean isBlockBoundary(Node n, Node parent) {
    if (parent != null) {
      switch (parent.getType()) {
        case Token.DO:
        case Token.FOR:
        case Token.TRY:
        case Token.WHILE:
        case Token.WITH:
          return true;
        case Token.AND:
        case Token.HOOK:
        case Token.IF:
        case Token.OR:
          return n != parent.getFirstChild();

      }
    }

    return n.isCase();
  }
